import React, {FC, ChangeEvent, useState} from 'react';
//import logo from './logo.svg';
import './App.css';

const App: FC = () => {

  const [task, SetTask] = useState<String>("")
  const [number, SetNumber] = useState<number>(0)
  const [task2, SetTask2] = useState<String>("")
  const [task3, SetTask3] = useState<String>("")
  const [task4, SetTask4] = useState([]) //vai ser uma lista

  const handleChange = (event: ChangeEvent<HTMLInputElement> ):void => {
    if(event.target.name === "task"){
      SetTask(event.target.value)
    }else{
      SetNumber(Number(event.target.value))
    }
  }

  const AddTask = ():void => {

    SetTask4([...task4,task])


  }
  return (
    <div className="App">
      <div className="Header">
        <div className="inputContainer">
          <input type="text" placeholder="texto aqui" name="task" onChange={handleChange}></input>
          <input type="number" placeholder="numero aqui" name="numero"></input>
          <input type="text" placeholder="texto aqui"></input>
          <input type="text" placeholder="texto aqui"></input>
          <input type="text" placeholder="texto aqui"></input>
        </div>
        <button onClick={AddTask} className="botao">Add Task</button>
      </div>
      <div className="todoList"></div>


    </div>
  );
}

export default App;
